function matchUsername(req, res, next) {
  /* Middleware ensures a user can not see other user's account */
  // console.log(req.session.username, req.params.username);
  if (req.session.username === req.params.username) {
    return next();
  }
  req.flash('error', 'Invalid user');
  return res.redirect('/');
}

module.exports = matchUsername;
